## SSH keys
include_recipe "ssh_known_hosts"

ssh_known_hosts_entry 'github.com'

ssh_known_hosts_entry 'moregithub1' do
    key "|1|bHwGKEeKu5MbT0WxZTWtKy13KQQ=|iM8dn4mT51ILd5QBjt2wsRIbTu0= ssh-rsa "
end

ssh_known_hosts_entry 'moregithub2' do
    key "|1|7+dCYySxVHZHRInFIpHSdwiw+pA=|7Jq5SeZaesAAXckuLQQKkYgQbc4= ssh-rsa "
end

service "ssh" do
  supports :status => true, :restart => true, :truereload => true
  action :nothing
end

cookbook_file "/etc/ssh/sshd_config" do
  owner "root"
  group "root"
  mode "0644"
  notifies :reload, "service[ssh]"
end
